<?php
require_once 'Agen.php';

// Memeriksa apakah metode permintaan adalah POST
if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    $id = $_POST['id']; // Mengambil ID agen dari formulir
    $agen = new Agen();
    $data = [
        'nama_agen' => $_POST['nama_agen'],
        'alamat' => $_POST['alamat'],
        'nomor_telepon' => $_POST['nomor_telepon'],
        'email' => $_POST['email'],
        'facebook' => $_POST['facebook'],
        'twitter' => $_POST['twitter'],
        'instagram' => $_POST['instagram'],
    ];

    // If the password is updated, set the new password
    if (!empty($_POST['password'])) {
        $data['password'] = password_hash($_POST['password'], PASSWORD_DEFAULT);
    }

    // Jika foto diperbarui, unggah foto baru
    if (isset($_FILES['foto_agen']['name']) && !empty($_FILES['foto_agen']['name'])) {
        $data['foto_agen'] = $_FILES['foto_agen'];
    }
 // Memanggil metode updateAgen dari objek Agen dengan data yang diperbarui
    $agen->updateAgen($id, $data);
    header('Location: agentampil.php');
    exit;
}
