<?php
require_once 'Testimoni.php';

if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    $id = $_POST['id']; // Assuming you have an input field for the agent ID in the form
    $testimoniObj = new Testimoni();
    $data = [
        'nama_pelanggan' => $_POST['nama_pelanggan'],
        'testimoni' => $_POST['testimoni'],
        'rating' => $_POST['rating'],
        'foto_testimoni' => $_FILES['foto_testimoni']
    ];
    // If the photo is updated, upload the new photo
    if (isset($_FILES['foto_testimoni']['name']) && !empty($_FILES['foto_testimoni']['name'])) {
        $data['foto_testimoni'] = $_FILES['foto_testimoni'];
    }

    $testimoniObj->updateTestimoni($id, $data);
    header('Location: testimonitampil.php');
    exit;
}
