<script>
    function setRating(rating) {
        document.getElementById('rating').value = rating;

        // Reset semua bintang ke warna awal
        for (let i = 1; i <= 5; i++) {
            document.getElementById('star' + i).classList.remove('selected');
        }

        // Berikan warna kuning pada bintang yang dipilih
        for (let i = 1; i <= rating; i++) {
            document.getElementById('star' + i).classList.add('selected');
        }
    }
</script>
